mapper.select:

 		select f.cr_id crID,f.lot lot,f.biobom bioBom,f.bio_pat_num bioPatNum,f.croperator1 crOperator1,f.croperator2 crOperator2,f.raw_material rawMaterial,f.in_put_date inPutDate,f.coating_station coatingStation,f.doc_rev docRev,
 		f.goods_qty goodsQty,f.auxiliary_reagent1 auxiliaryReagent1,f.auxiliary_reagent2 auxiliaryReagent2,f.auxiliary_reagent3 auxiliaryReagent3,f.input_qty inputQty,f.scrap_qty scrapQty,f.receive_qty receiveQty,f.heavy_smoke_qty heavySmokeQty,f.to_pqc_qty toPqcQty,f.to_other_qty toOtherQty,
 		f.remark,f.part_num partNum,f.theory_yield theoryYield,f.actual_yield actualYield,f.theory_actual_yield theoryActualYield,f.input_time inputTime,f.update_time updateTime,
        f.operatorid,g.account operatorName from t_ums_workflow_biocoating f left join t_ums_admininfo g on f.operatorid=g.operatorid where 1=1 

	       AND f.lot = #{lot}  


	      AND f.in_put_date >= #{startInPutDate}  


	       AND f.in_put_date <= #{endInPutDate}  


	       AND f.cr_id = #{crID}  


	       AND f.bio_pat_num = #{bioPatNum}  


	       AND exists(select 1 from t_ums_workflow_biocoating_relation a where a.cr_id=f.cr_id and a.t_lot_num=#{tNum})  


	       AND exists(select 1 from t_ums_workflow_biocoating_relation b where b.cr_id=f.cr_id and b.tray_lot_num=#{trayLotNum})  


	       AND exists(select 1 from t_ums_workflow_biocoating_reagents c where c.cr_id=f.cr_id and c.reagents_sn=#{chemicalLotNum})  

	     order by f.update_time desc limit #{startPageNum},#{pageSize}


 		select count(1) from t_ums_workflow_biocoating f  where 1=1

	       AND f.lot = #{lot}  


	      AND f.in_put_date >= #{startInPutDate}  


	       AND f.in_put_date <= #{endInPutDate}  


	       AND f.cr_id = #{crID}  


	       AND f.bio_pat_num = #{bioPatNum}  


	       AND exists(select 1 from t_ums_workflow_biocoating_relation a where a.cr_id=f.cr_id and a.t_lot_num=#{tNum})  


	       AND exists(select 1 from t_ums_workflow_biocoating_relation b where b.cr_id=f.cr_id and b.tray_lot_num=#{trayLotNum})  


	       AND exists(select 1 from t_ums_workflow_biocoating_reagents c where c.cr_id=f.cr_id and c.reagents_sn=#{chemicalLotNum})

select a.cr_id crID,group_concat(a.tray_lot_num) trayLotNum,group_concat(a.t_lot_num) tLotNum,group_concat(distinct f.fixture_num) fixtrueNums,group_concat(distinct f.output_lot_num) assemblyLotNum,group_concat(distinct f.assembly_bom) assemblyBom,group_concat(distinct b.output_lot_num) apsLotNum,group_concat(distinct b.sf_bom) apsBom,group_concat(distinct c.output_lot_num) ocLotNum,group_concat(distinct c.oc_bom) ocBom,group_concat(distinct d.clean_lot_num) cleanLotNum,group_concat(distinct d.clean_bom) cleanBom,group_concat(distinct e.polish_lot_num) polishLotNum,group_concat(distinct e.polish_bom) polishBom from t_ums_workflow_biocoating_relation a LEFT JOIN t_ums_workflow_assembly f ON f.tray_lot_num = a.tray_lot_num LEFT JOIN t_ums_workflow_platedfilm b ON f.input_lot_num = b.output_lot_num and f.fixture_num=b.fixture_num LEFT JOIN t_ums_workflow_opticalfilming c ON c.output_lot_num = b.input_lot_num and c.fixture_num=b.fixture_num LEFT JOIN t_ums_workflow_clean d ON d.clean_lot_num = c.input_lot_num and d.fixture_number=c.fixture_num LEFT JOIN t_ums_workflow_polish e ON e.polish_lot_num = d.input_lot_num and e.fixture_number=d.fixture_number where a.cr_id in
#{item}
GROUP BY a.cr_id


 		select f.cr_id crID,f.reagents_field_name reagentsFieldName,f.main_reagent mainReagent,f.reagents_name reagentsName,f.reagents_sn reagentsSn from t_ums_workflow_biocoating_reagents f where 1=1 

	       AND f.cr_id = #{crID}  

	     order by f.main_reagent desc limit #{startPageNum},#{pageSize}
