mapper.select:

 		select f.btnid,f.menuid,g.menuname,f.btnename,f.btncname,f.btnclass,f.btniconclass,f.DESCRIPTION from t_ums_menu_button f left join T_UMS_MENUINFO g on f.menuid=g.menuid where 1=1

	       AND btnename like '%#{btnEName}%'


	       AND btncname like '%#{btnCName}%'


	       AND menuid=#{menuid, jdbcType=INTEGER}

	     order by f.menuid desc limit #{startPageNum},#{pageSize}

mapper.delete:

        DELETE FROM t_ums_menu_button WHERE 1=1

	       AND 1=2   


	       AND btnid in

	        	#{item}  



	       menuid in 

	        	#{item}
