mapper.select:

 		select OPERATORID,NAME,ACCOUNT,PASSWORD,SEX,POSITION,DEPTID,MOBILE,EMAIL,GROUPLIST,PROVINCEID,OPERATORTYPE,STATUS,UPDATEPWDFLAG,UPDATETIME from t_ums_admininfo f where 1=1 

	       AND f.ACCOUNT = #{userName}  


	       AND f.OPERATORID = #{operatorid,jdbcType=INTEGER}


 		select OPERATORID,NAME,ACCOUNT,SEX,POSITION,DEPTID,MOBILE,EMAIL,GROUPLIST,PROVINCEID,OPERATORTYPE,STATUS,UPDATEPWDFLAG,str_to_date(createtime,'%Y-%m-%d %H:%i:%s')createtime,str_to_date(UPDATETIME,'%Y-%m-%d %H:%i:%s')updatetime from t_ums_admininfo a 
 		where 1=1

	       AND a.ACCOUNT = #{account}  


	       AND a.OPERATORTYPE = #{operatorType,jdbcType=INTEGER}  


	       AND a.MOBILE = #{mobile}  

 		 order by a.updatetime desc limit #{startPageNum},#{pageSize}


 		select count(1) from t_ums_admininfo a 
 		where 1=1

	       AND a.ACCOUNT = #{account}  


	       AND a.OPERATORTYPE = #{operatorType}  


	       AND a.MOBILE = #{mobile}
