mapper.select:

 		select assemble_time assembleTime,production_time productionTime,cistern_pn cisternPn,serial_no serialNo,workorder_no workorderNo,cistern_model cisternModel,exception_record exceptionRecord,assemble_Operator assembleOperator,f.input_time inputTime,f.update_time updateTime,
f.operatorid,g.account operatorName from t_trackinglist_cistern f left join t_ums_admininfo g on f.operatorid=g.operatorid where 1=1 

	       AND f.assemble_time >= #{startAssembleTime}  


	       AND f.assemble_time <= #{endAssembleTime}  


	       AND f.cistern_id=#{cisternID}  


	       AND f.cistern_pn =#{cisternPn}   


	       AND f.serial_no = #{serialNo}  


	       AND f.workorder_no = #{workorderNo}  


	       AND f.cistern_model = #{cisternModel}  

	     order by f.update_time desc limit #{startPageNum},#{pageSize}


 		select count(1) from t_trackinglist_cistern f  where 1=1

	       AND f.assemble_time >= #{startAssembleTime}  


	       AND f.assemble_time <= #{endAssembleTime}  


	       AND f.cistern_id=#{cisternID}  


	       AND f.cistern_pn =#{cisternPn}   


	       AND f.serial_no = #{serialNo}  


	       AND f.workorder_no = #{workorderNo}  


	       AND f.cistern_model = #{cisternModel}

mapper.update:
update  t_trackinglist_cistern f set f.assemble_time=#{assembleTime},f.production_time=#{productionTime},f.cistern_pn=#{cisternPn},f.serial_no=#{serialNo},f.workorder_no=#{workorderNo},f.cistern_model=#{cisternModel},f.exception_record=#{exceptionRecord},
f.assemble_Operator=#{assembleOperator},f.operatorid=#{operatorid},f.update_time=now()
where f.cistern_id=#{cisternID}
