mapper.select:

 		select assemble_time assembleTime,production_time productionTime,dsp_pn dspPn,serial_no serialNo,workorder_no workorderNo,dsp_model dspModel,exception_record exceptionRecord,assemble_Operator assembleOperator,f.input_time inputTime,f.update_time updateTime,
f.operatorid,g.account operatorName from t_trackinglist_dsp f left join t_ums_admininfo g on f.operatorid=g.operatorid where 1=1 

	       AND f.assemble_time >= #{startAssembleTime}  


	       AND f.assemble_time <= #{endAssembleTime}  


	       AND f.dsp_id=#{dspID}  


	       AND f.dsp_pn =#{dspPn}   


	       AND f.serial_no = #{serialNo}  


	       AND f.workorder_no = #{workorderNo}  


	       AND f.dsp_model = #{dspModel}  

	     order by f.update_time desc limit #{startPageNum},#{pageSize}


 		select count(1) from t_trackinglist_dsp f  where 1=1

	       AND f.assemble_time >= #{startAssembleTime}  


	       AND f.assemble_time <= #{endAssembleTime}  


	       AND f.dsp_id=#{dspID}  


	       AND f.dsp_pn =#{dspPn}   


	       AND f.serial_no = #{serialNo}  


	       AND f.workorder_no = #{workorderNo}  


	       AND f.dsp_model = #{dspModel}

mapper.update:
update  t_trackinglist_dsp f set f.assemble_time=#{assembleTime},f.production_time=#{productionTime},f.dsp_pn=#{dspPn},f.serial_no=#{serialNo},f.workorder_no=#{workorderNo},f.dsp_model=#{dspModel},f.exception_record=#{exceptionRecord},
f.assemble_Operator=#{assembleOperator},f.operatorid=#{operatorid},f.update_time=now()
where f.dsp_id=#{dspID}
